package com.company.study.ch02;

public class OperationsPromotionExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
//		byte + byte = int
		byte byteValue = 12;
		byte byteValue2 = 12;
		int intValue3 = byteValue + byteValue2 ;
		System.out.println(intValue3);
		
		
//		short + short = int
		short shortValue = 12;
		short shortValue2 = 12;
		int intValue4 = shortValue + shortValue2 ;
		System.out.println(intValue4);
		
		
		int  intValue = 12;
		int intValue2 = 12;
		int intValue5 = intValue + intValue2 ;
		System.out.println(intValue5);
		
		
		long longValue = 12;
		long longValue2 = 12;
		long longValue3 = longValue + longValue2 ;
		System.out.println(longValue3);
		
		
		float floatValue = 12;
		float floatValue2 = 12;
		float floatValue3 = floatValue + floatValue2 ;
		System.out.println(floatValue3);
		

		double doubleValue = 12;
		double doubleValue2 = 12;
		double doubleValue3 = doubleValue + doubleValue2 ;
		System.out.println(doubleValue3);
		
		
		
		
		
//		int + byte = int
//		byte byteValue4 = byteValue + intValue;   (error)
		int intValue6 = byteValue + intValue;
		System.out.println(intValue4);
		
		
//		int + double = double
		
//		int intValue7 = intValue + doubleValue;  (error)
		double intValue8 = intValue + doubleValue;
		System.out.println(intValue8);
		
		
		
		
		long test = 1234567890;
		byte test2 = (byte)test;
		System.out.println(test2);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
